var Thread = require('child-manager');

module.exports = {
    word: function(word, limit, filePath, rootPath, callback){
        var flag = true
        var thread = new Thread(rootPath + 'node_modules/searching/process.js', function (out) {
        if(flag){
            callback(out);
            flag = false
            thread.close();
            }
        }, 1);
        thread.execute([word, limit, filePath])
    }
}
