
var searching = require('./main.js');
var expect = require('unexpected');

describe('timestamping', function () {
    this.timeout(15000);
    it('should test main method', function (done) {
          searching.word('Aristoteles',6,'testFile.txt', '', function(data){
            expect(data, 'to be an array')
            expect(data.length, 'to be', 6)
            expect(data[0].indexOf('Aristoteles'), 'to be greater than', -1)
            done()
          })  
    });
    it('should reach end of file', function (done) {
          searching.word('Aristoteles',5000,'testFile.txt', '', function(data){
            expect(data, 'to be an array')
            expect(data.length, 'to be greater than', 6)
            expect(data[0].indexOf('Aristoteles'), 'to be greater than', -1)
            done()
          })  
    });
});
